mapper.select:
SELECT board_userno , boardno , board_title , board_content , board_ani_category , board_reg_date , board_viewcount , commentcount , likecount , fileAttached
FROM board
WHERE boardno = #{boardno}

mapper.update:
UPDATE board
SET board_title = #{boardDto.board_title} , board_content = #{boardDto.board_content} , board_ani_category = #{boardDto.board_ani_category} , fileAttached = #{boardDto.fileAttached}
WHERE boardno = #{boardDto.boardno} and board_userno = #{LoginId}

UPDATE board
SET fileAttached = #{fileAttached}
WHERE boardno = #{boardno}

UPDATE board
SET board_viewcount = board_viewcount + 1
WHERE boardno = #{boardno}

Update board
SET commentcount = commentcount + #{num}
Where boardno = #{boardno}
