mapper.select:
SELECT mapboard_userno , mapboardno , mapboard_content , mapboard_imagepath , missingtime , missingaddress , latitude , longitude , mapboard_reg_date , mapboard_viewcount , mapboard_ani_category , writertype FROM mapboard WHERE TRUE and
and
ORDER BY mapboardno DESC LIMIT #{offset} , #{pageSize}

SELECT mapboard_userno , mapboardno , mapboard_content , mapboard_imagepath , missingtime , missingaddress , latitude , longitude , mapboard_reg_date , mapboard_viewcount , mapboard_ani_category , writertype
FROM mapboard
WHERE mapboardno = #{mapboardno}

mapper.update:
UPDATE mapboard
SET mapboard_imagepath = #{mapboard_imagepath} , missingtime = #{missingtime} , missingaddress = #{missingaddress} , latitude = #{latitude} , longitude = #{longitude} , mapboard_ani_category = #{mapboard_ani_category} , writertype = #{writertype}
WHERE mapboard_userno = #{mapboard_userno} and mapboardno = #{mapboardno}
