mapper.select:
SELECT u.id, mb.mapboard_userno , mb.mapboardno , mb.mapboard_title, mb.mapboard_content , mb.missingtime , mb.missingaddress , mb.latitude , mb.longitude , mb.mapboard_reg_date , mb.mapboard_viewcount , mb.mapboard_ani_category , mb.writertype, mb.fileAttached FROM mapboard mb, users u WHERE mb.mapboard_userno = u.userno and mb.missingaddress LIKE concat('%' , #{keyword} ,'%') and
and
ORDER BY mb.mapboardno DESC LIMIT #{offset} , #{pageSize}

SELECT u.id , mb.mapboard_userno , mb.mapboardno , mb.mapboard_title , mb.mapboard_content , mb.missingtime , mb.missingaddress , mb.latitude , mb.longitude , mb.mapboard_reg_date , mb.mapboard_viewcount , mb.mapboard_ani_category , mb.writertype, mb.fileAttached
FROM mapboard mb , users u
WHERE mb.mapboard_userno = u.userno and mb.mapboardno = ${mapboardno}

mapper.update:
UPDATE mapboard
SET mapboard_title = #{mapboard_title} , mapboard_content = #{mapboard_content} , missingtime = #{missingtime} , missingaddress = #{missingaddress} , latitude = #{latitude} , longitude = #{longitude} , mapboard_ani_category = #{mapboard_ani_category} , writertype = #{writertype} , fileAttached = #{fileAttached}
WHERE mapboard_userno = #{mapboard_userno} and mapboardno = #{loginUser}

UPDATE mapboard
SET fileAttached = #{fileAttached}
WHERE mapboardno = #{mapboardno}

mapper.select:
SELECT u.id, mb.mapboard_userno , mb.mapboardno , mb.mapboard_title, mb.mapboard_content ,  mb.missingtime , mb.missingaddress , mb.latitude , mb.longitude , mb.mapboard_reg_date , mb.mapboard_viewcount , mb.mapboard_ani_category , mb.writertype, mb.fileAttached
FROM mapboard mb, users u
WHERE mb.mapboard_userno = u.userno and mb.mapboard_userno = #{userno}
