mapper.select:
SELECT protectboard_userno , protectboardno ,  protectboard_title , protectboard_content , protectboard_imagepath , breeds , protectboard_ani_category , protectboard_reg_date , protectstatus , starttime , deadline
FROM protectboard
WHERE protectboard_ani_category = #{protectboard_ani_category}
ORDER BY deadline ASC , protectboardno ASC
LIMIT #{offset} , #{pageSize}

SELECT protectboard_userno , protectboardno , protectboard_title ,protectboard_content , protectboard_imagepath , breeds , protectboard_ani_category , protectboard_reg_date , protectstatus , starttime , deadline
FROM protectboard
WHERE protectboardno = #{protectboardno}

mapper.update:
UPDATE protectboard
SET protectboard_title = #{protectboard_title} , protectboard_content = #{protectboard_content} , protectboard_imagepath = #{protectboard_imagepath} ,  breeds = #{breeds} , protectboard_ani_category = #{protectboard_ani_category} , protectstatus = #{protectstatus}
WHERE protectboard_userno = #{protectboard_userno} and protectboardno = #{protectboardno}
